import { Component, TemplateRef, ViewChild } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { BsModalRef, BsModalService } from 'ngx-bootstrap/modal';
import { FormatterDirective } from '../util/formatter.directive';
import { CadatroContaComponent } from './cadatro-conta/cadatro-conta.component';
import { NgbModal } from '@ng-bootstrap/ng-bootstrap';
import { Modal2Component } from './modal2/modal2.component';
@Component({
  selector: 'app-finances',
  templateUrl: './finances.component.html',
  styleUrls: ['./finances.component.css']
})
export class FinancesComponent {

  formulario:FormGroup;
  conta:{};



  //referencia a outro componeente
  // @ViewChild(CadatroContaComponent)
  // cadastroConta:CadatroContaComponent | undefined;



  constructor(private modalService: NgbModal) {
    this.formulario = new FormGroup({
      descricao: new FormControl(null,Validators.required),
      valor: new FormControl(null,Validators.required),
      formaPagamento: new FormControl('CARTAO',Validators.required),
    })
  }
  verificaValidTouched(campo:string){
    return !this.formulario.get(campo)?.valid && this.formulario.get(campo)?.touched;
  }

  teste(){
    this.modalService.open(Modal2Component);
  }
}
<div class="container main">
  <div class="row">
    <div class="col-md-6">
      <app-card-gasto titulo="Saldo Conta Corrente" [saldo]=1000.00 infoRight="02/06/2023"></app-card-gasto>
    </div>
    <div class="col-md-6">
      <app-card-gasto titulo="Saldo Poupança" [saldo]=1000.0></app-card-gasto>
    </div>
  </div>
  <div class="row" style="margin-top: 20px;">
    <div class="col-md-12">
      <app-table-gasto></app-table-gasto>
    </div>
  </div>
  <!-- <div class="row" style="margin-top: 30px;">
    <button class="btn btn-success" (click)="openModalAdicionalConta()">Abir Modal</button>
    <app-cadatro-conta #cadastroConta (save)="adicionaConta($event)" [conta]="conta" ></app-cadatro-conta>
  </div> -->
  <div class="row">
    <button class="btn btn-default" (click)="teste()">TESTE</button>
  </div>
  <div class="row">
    <p>{{conta | json}}</p>
  </div>
</div>
